pragma solidity 0.8.11;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Context.sol";

// Tests for this contract are in the BasicToken.test.ts file
abstract contract Blacklist is Context {
    mapping(address => bool) blacklist;
    mapping(address => bool) whitelist;

    bool public isWhitelistRestrictionMode;

    event BlacklistAdded(address[] _addresses, address admin);
    event BlacklistRemoved(address[] _addresses, address admin);
    event WhitelistAdded(address[] _addresses, address admin);
    event WhitelistRemoved(address[] _addresses, address admin);
    event WhitelistEnabled(address admin);
    event WhitelistDisabled(address admin);

    function _addToBlacklist(address[] memory _addresses) internal virtual {
        for (uint256 i = 0; i < _addresses.length; i++) {
            blacklist[_addresses[i]] = true;
        }
        emit BlacklistAdded(_addresses, _msgSender());
    }

    function _removeFromBlacklist(address[] memory _addresses)
        internal
        virtual
    {
        for (uint256 i = 0; i < _addresses.length; i++) {
            blacklist[_addresses[i]] = false;
        }
        emit BlacklistRemoved(_addresses, _msgSender());
    }

    function _addToWhitelist(address[] memory _addresses) internal virtual {
        for (uint256 i = 0; i < _addresses.length; i++) {
            whitelist[_addresses[i]] = true;
        }
        emit WhitelistAdded(_addresses, _msgSender());
    }

    function _removeFromWhitelist(address[] memory _addresses)
        internal
        virtual
    {
        for (uint256 i = 0; i < _addresses.length; i++) {
            whitelist[_addresses[i]] = false;
        }
        emit WhitelistRemoved(_addresses, _msgSender());
    }

    // --------- Helper functions ---------
    function isAddressInBlacklist(address _address) public view returns (bool) {
        return blacklist[_address];
    }

    function isAddressInWhiteList(address _address) public view returns (bool) {
        return whitelist[_address];
    }

    // --------- Administrative functions ---------
    function _onWhitelistMode() internal virtual {
        isWhitelistRestrictionMode = true;
        emit WhitelistEnabled(_msgSender());
    }

    function _offWhitelistMode() internal virtual {
        isWhitelistRestrictionMode = false;
        emit WhitelistDisabled(_msgSender());
    }
}
","keccak256":"0xc0679b30934541a57cebe466cffabbfa33a0b7af2b29203531f88ee04fc29e8c","license":"MIT"},"contracts/extensions/ERC20Blacklist.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./Blacklist.sol";

abstract contract ERC20Blacklist is ERC20, Blacklist {
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        if (isWhitelistRestrictionMode) {
            require(
                whitelist[from] || whitelist[to],
                "Whitelist: sender or receiver is not in whitelist"
            );
        }
        require(!blacklist[from], "Blacklist: sender is in blacklist");
        require(!blacklist[to], "Blacklist: receiver is in blacklist");
        if (from != _msgSender()) {
            require(
                !blacklist[_msgSender()],
                "Blacklist: spender is in blacklist"
            );
        }
        super._beforeTokenTransfer(from, to, amount);
    }
}
","keccak256":"0x73e5ae94d5996f01dc3029ecfc861a7bda08f7c2ae9f63af108f1b18b8d440ac","license":"MIT"},"contracts/tokens/BasicToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Snapshot.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "../extensions/ERC20Blacklist.sol";

contract BasicToken is
    ERC20,
    ERC20Burnable,
    ERC20Blacklist,
    AccessControl,
    Pausable
{
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    uint256 private _totalMinted;

    constructor(
        uint256 initialSupply_,
        address holderAddress_,
        string memory name_,
        string memory symbol_
    ) ERC20(name_, symbol_) {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _grantRole(PAUSER_ROLE, _msgSender());

        _mint(holderAddress_, initialSupply_);
    }

    function totalBurn() public view virtual returns (uint256) {
        return _totalMinted - totalSupply();
    }

    function totalMinted() public view virtual returns (uint256) {
        return _totalMinted;
    }

    function _mint(address account, uint256 amount) internal virtual override {
        super._mint(account, amount);
        _totalMinted += amount;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20Blacklist, ERC20) whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _checkRole(bytes32 role, address account)
        internal
        view
        virtual
        override
    {
        if (!hasRole(role, account) && !hasRole(DEFAULT_ADMIN_ROLE, account)) {
            revert(
                string(
                    abi.encodePacked(
                        "AccessControl: account ",
                        Strings.toHexString(uint160(account), 20),
                        " is missing role ",
                        Strings.toHexString(uint256(role), 32),
                        " or ",
                        Strings.toHexString(uint256(DEFAULT_ADMIN_ROLE), 32)
                    )
                )
            );
        }
    }

    // --------- Administrative functions ---------
    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function onWhitelistMode() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _onWhitelistMode();
    }

    function offWhitelistMode() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _offWhitelistMode();
    }

    function addToBlacklist(address[] calldata _addresses)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _addToBlacklist(_addresses);
    }

    function removeFromBlacklist(address[] calldata _addresses)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _removeFromBlacklist(_addresses);
    }

    function addToWhitelist(address[] calldata _addresses)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _addToWhitelist(_addresses);
    }

    function removeFromWhitelist(address[] calldata _addresses)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _removeFromWhitelist(_addresses);
    }
}
