pragma solidity 0.8.11;

import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Snapshot.sol";
import "./BasicToken.sol";

contract Token1 is BasicToken, ERC20Snapshot {
    bytes32 public constant SNAPSHOT_ROLE = keccak256("SNAPSHOT_ROLE");

    constructor(uint256 initialSupply_, address holderAddress_)
        BasicToken(initialSupply_, holderAddress_, "Test SAV", "TSAV")
    {
        _grantRole(SNAPSHOT_ROLE, _msgSender());
    }

    function snapshot() public onlyRole(SNAPSHOT_ROLE) {
        _snapshot();
    }

    function snapshotCount()
        public
        view
        onlyRole(SNAPSHOT_ROLE)
        returns (uint256)
    {
        return _getCurrentSnapshotId();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(BasicToken, ERC20Snapshot) whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _mint(address account, uint256 amount)
        internal
        virtual
        override(BasicToken, ERC20)
    {
        super._mint(account, amount);
    }
}

contracts/ReferralManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./interfaces/IReferralManager.sol";

contract ReferralManager is IReferralManager, AccessControl {
    uint256 public constant LEVELS = 10;
    uint256 public SUBSCRIPTION_PERIOD_DAYS = 365;
    uint256[] public REFERRAL_PERCENTS = [
        100,
        90,
        80,
        70,
        60,
        50,
        40,
        30,
        20,
        10
    ];

    struct User {
        address referrer;
        uint256[LEVELS] activeLevels;
        uint256 totalRefDividends;
        uint256 totalRefDividendsClaimed;
        address[] referrals_1_lvl;
        uint256[LEVELS] refCount;
        bool isActive;
    }

    struct Referral {
        address referralAddress;
        uint256 level;
    }

    mapping(address => User) private users;
    mapping(address => bool) private authorizedContracts;

    ERC20Burnable public subscriptionToken;
    IERC20 public rewardToken;
    address private rewardPool;
    uint256 public levelSubscriptionCost;
    uint256 public fullSubscriptionCost;

    event Subscribed(
        address indexed subscriber,
        uint256 levels,
        uint256 indexed timestamp
    );
    event ReferralAdded(address indexed referrer, address indexed referral);

    constructor(
        address subscriptionToken_,
        address rewardToken_,
        address rewardPool_,
        uint256 fullSubscriptionCost_,
        uint256 levelSubscriptionCost_
    ) {
        require(subscriptionToken_ != address(0));
        require(rewardToken_ != address(0));
        require(rewardPool_ != address(0));
        require(fullSubscriptionCost_ > 0);
        require(levelSubscriptionCost_ > 0);

        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());

        subscriptionToken = ERC20Burnable(subscriptionToken_);
        rewardToken = IERC20(rewardToken_);
        rewardPool = rewardPool_;
        fullSubscriptionCost = fullSubscriptionCost_;
        levelSubscriptionCost = levelSubscriptionCost_;
    }

    function subscribeToLevels(uint256 levels) public {
        require(levels > 0, "Too low levels");
        require(levels <= LEVELS, "Too much levels");

        uint256 price = levels * levelSubscriptionCost;
        subscriptionToken.burnFrom(_msgSender(), price);

        _setSubscription(_msgSender(), levels);
    }

    function subscribeToAllLevels() public {
        subscriptionToken.burnFrom(_msgSender(), fullSubscriptionCost);
        _setSubscription(_msgSender(), LEVELS);
    }

    function addUserDividends(address user, uint256 reward)
        public
        onlyAuthorizedContracts
    {
        users[user].totalRefDividends += reward;
    }

    function claimDividends(uint256 amount) public {
        User storage user = users[_msgSender()];
        require(
            user.totalRefDividends - user.totalRefDividendsClaimed >= amount,
            "Insufficient amount"
        );

        user.totalRefDividendsClaimed += amount;
        rewardToken.transferFrom(rewardPool, _msgSender(), amount);
    }

    function setUserReferrer(address user, address referrer)
        public
        onlyAuthorizedContracts
    {
        require(user != address(0), "User is zero address");
        require(referrer != address(0), "Referrer is zero address");
        require(referrer != user, "Referrer can not be user");
        require(
            users[user].referrer == address(0),
            "Referrer is already specified"
        );
        require(
            userHasSubscription(referrer, 1),
            "Referrer has no subscription"
        );

        users[user].referrer = referrer;
        users[referrer].referrals_1_lvl.push(user);

        address nextReferrer = referrer;
        for (uint256 i = 0; i < LEVELS; i++) {
            User storage ref = users[nextReferrer];
            if (ref.isActive) {
                ref.refCount[i] += 1;
                nextReferrer = ref.referrer;
            } else break;
        }

        emit ReferralAdded(referrer, user);
    }

    function _setSubscription(address subscriber, uint256 levels) internal {
        uint256 subscriptionEnd = block.timestamp +
            SUBSCRIPTION_PERIOD_DAYS *
            1 days;

        for (uint256 i = 0; i < levels; i++) {
            users[subscriber].activeLevels[i] = subscriptionEnd;
        }

        users[subscriber].isActive = true;

        emit Subscribed(subscriber, levels, block.timestamp);
    }

    // --------- Helper functions ---------
    function getReferralLevels() public pure returns (uint256) {
        return LEVELS;
    }

    function getUserInfo(address userAddress)
        public
        view
        returns (
            address referrer,
            uint256 totalDividends,
            uint256 totalClaimedDividends,
            uint256 referrals_1_lvl,
            uint256 totalReferrals
        )
    {
        User storage user = users[userAddress];

        referrer = user.referrer;
        totalDividends = user.totalRefDividends;
        totalClaimedDividends = user.totalRefDividendsClaimed;
        referrals_1_lvl = user.referrals_1_lvl.length;
        totalReferrals = _getUserReferralsCount(userAddress, 0);
    }

    function getUserReferrer(address user) public view returns (address) {
        return users[user].referrer;
    }

    function getUser1LvlReferrals(address userAddress)
        public
        view
        returns (address[] memory)
    {
        return users[userAddress].referrals_1_lvl;
    }

    function _getUserReferralsCount(address userAddress, uint256 currentLevel)
        internal
        view
        returns (uint256)
    {
        User storage user = users[userAddress];
        uint256 referralCounter = 0;

        for (uint256 i = 0; i < LEVELS - currentLevel; i++) {
            referralCounter += user.refCount[i];
        }

        return referralCounter;
    }

    // TODO: add staking info to referrals
    function getUserReferrals(address userAddress, uint256 currentLevel)
        public
        view
        returns (Referral[] memory)
    {
        uint256 referralsCount = _getUserReferralsCount(
            userAddress,
            currentLevel
        );

        uint256 nextReferralIndex = 0;
        Referral[] memory referrals = new Referral[](referralsCount);

        address[] memory level1Referrals = getUser1LvlReferrals(userAddress);

        for (uint256 i = 0; i < level1Referrals.length; i++) {
            referrals[nextReferralIndex] = Referral(
                level1Referrals[i],
                currentLevel + 1
            );
            nextReferralIndex++;

            if (currentLevel + 1 < LEVELS) {
                Referral[] memory theirReferrals = getUserReferrals(
                    level1Referrals[i],
                    currentLevel + 1
                );

                for (uint256 j = 0; j < theirReferrals.length; j++) {
                    referrals[nextReferralIndex] = theirReferrals[j];
                    nextReferralIndex++;
                }
            }
        }

        return referrals;
    }

    function userHasSubscription(address user, uint256 level)
        public
        view
        returns (bool)
    {
        return users[user].activeLevels[level - 1] > block.timestamp;
    }

    function calculateRefReward(uint256 amount, uint256 level)
        public
        view
        returns (uint256)
    {
        require(level > 0);
        require(level <= LEVELS);
        return (amount * REFERRAL_PERCENTS[level - 1]) / 100;
    }

    function isAuthorized(address contractAddress) public view returns (bool) {
        return authorizedContracts[contractAddress];
    }

    modifier onlyAuthorizedContracts() {
        require(isAuthorized(_msgSender()), "Address not authorized");
        _;
    }

    // --------- Administrative functions ---------

    function authorizeContract(address contractAddress)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        authorizedContracts[contractAddress] = true;
    }

    function removeContractAuthorization(address contractAddress)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        authorizedContracts[contractAddress] = false;
    }

    function updateSubscriptionPeriod(uint256 durationDays)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        SUBSCRIPTION_PERIOD_DAYS = durationDays;
    }

    function updateReferralPercent(uint256 level, uint256 percent)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(level > 0);
        require(level <= LEVELS);
        REFERRAL_PERCENTS[level - 1] = percent;
    }

    function updateSubscriptionToken(address token)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        subscriptionToken = ERC20Burnable(token);
    }

    function updateRewardToken(address token)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        rewardToken = IERC20(token);
    }

    function updateRewardPool(address poolAddress)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        rewardPool = poolAddress;
    }

    function updateLevelSubscriptionCost(uint256 cost)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        levelSubscriptionCost = cost;
    }

    function updateFullSubscriptionCost(uint256 cost)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        fullSubscriptionCost = cost;
    }
}

contracts/VendorSell.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract VendorSell is Context, AccessControl, Pausable {
    using SafeERC20 for IERC20;

    uint256 private SWAP_RATE_DIVIDER = 1000;
    uint256 public swapRate; // divide on SWAP_RATE_DIVIDER
    bool private _isSellAvailable;

    IERC20 public token;
    address private _tokenPool;
    IERC20 public changeToken;
    address private _changeTokenPool;

    event TokensPurchased(
        address indexed buyer,
        uint256 amountToken,
        uint256 amountChangeToken
    );
    event TokensSold(
        address indexed seller,
        uint256 amountToken,
        uint256 amountChangeToken
    );

    constructor(
        address tokenAddress_,
        address tokenPool_,
        address changeTokenAddress_,
        address changeTokenPool_,
        uint256 swapRate_
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        swapRate = swapRate_;

        token = IERC20(tokenAddress_);
        changeToken = IERC20(changeTokenAddress_);

        _tokenPool = tokenPool_;
        _changeTokenPool = changeTokenPool_;
    }

    // @params: _amountChangeToken - how much user spend
    function buyTokens(uint256 _amountChangeToken) public whenNotPaused {
        uint256 _amountToken = getEquivalentTokenEstimate(_amountChangeToken);
        require(_amountToken > 0, "Insufficient amount");

        require(
            changeToken.transferFrom(
                _msgSender(),
                _changeTokenPool,
                _amountChangeToken
            ),
            "Change token transfer error"
        );
        require(
            token.transferFrom(_tokenPool, _msgSender(), _amountToken),
            "Token transfer error"
        );

        emit TokensPurchased(_msgSender(), _amountToken, _amountChangeToken);
    }

    // @params: _amountToken - how much user spend
    function sellTokens(uint256 _amountToken) public whenNotPaused {
        require(_isSellAvailable, "Selling is not available");

        uint256 _amountChangeToken = getEquivalentChangeTokenEstimate(
            _amountToken
        );
        require(_amountChangeToken > 0, "Insufficient amount");

        require(
            token.transferFrom(_msgSender(), _tokenPool, _amountToken),
            "Token transfer error"
        );
        require(
            changeToken.transferFrom(
                _changeTokenPool,
                _msgSender(),
                _amountChangeToken
            ),
            "Change token transfer error"
        );

        emit TokensSold(_msgSender(), _amountToken, _amountChangeToken);
    }

    // --------- Helper functions ---------
    function getTokenReserve() public view returns (uint256) {
        return token.balanceOf(_tokenPool);
    }

    function getChangeTokenReserve() public view returns (uint256) {
        return changeToken.balanceOf(_changeTokenPool);
    }

    // !important: hope that decimals of tokens are the same
    function getEquivalentChangeTokenEstimate(uint256 _amountToken)
        public
        view
        returns (uint256)
    {
        return (_amountToken * SWAP_RATE_DIVIDER) / swapRate;
    }

    // !important: hope that decimals of tokens are the same
    function getEquivalentTokenEstimate(uint256 _amountChangeToken)
        public
        view
        returns (uint256)
    {
        return (_amountChangeToken * swapRate) / SWAP_RATE_DIVIDER;
    }

    function isSellAvailable() public view returns (bool) {
        return _isSellAvailable;
    }

    // --------- Administrative functions ---------
    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function enableSell() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _isSellAvailable = true;
    }

    function disableSell() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _isSellAvailable = false;
    }

    function updateTokenPool(address pool) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _tokenPool = pool;
    }

    function updateSwapRate(uint256 swapRate_)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        swapRate = swapRate_;
    }

    function updateChangeTokenPool(address pool)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _changeTokenPool = pool;
    }

    function updateToken(IERC20 token_) public onlyRole(DEFAULT_ADMIN_ROLE) {
        token = token_;
    }

    function updateChangeToken(IERC20 token_)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        changeToken = token_;
    }
}

